function add_message(a){jQuery("#wpbody-content").append("<p>"+a+"</p>")}function run_upgrade(a){jQuery.ajax({url:ajaxurl,data:{action:"cpac_upgrade",version:a},type:"post",dataType:"json",success:function(b){if(b){if(b.status){add_message(b.message);if(b.next){run_upgrade(b.next)}else{add_message(cpac_upgrade_i18n.complete)}}else{add_message(cpac_upgrade_i18n.error+": "+b.message)}}else{add_message(cpac_upgrade_i18n.major_error)}}})};